#!/bin/bash
echo "Bootstrap Studio"
if [ "$1" == "--help" ]; then
  echo "Usage: $0 [--install] [--uninstall] [--help]"
  echo "  --install: Install Bootstrap Studio"
  echo "    -y: confirm all prompts and you won't be asked for any confirmation "
  echo "  --uninstall: Uninstall Bootstrap Studio"
  echo "    -y: confirm all prompts you won't be asked for any confirmation "
  echo "  --help: Show this help"
  exit 0
fi
if ! [ "$EUID" -ne 0 ]; then
  BS_DIR=/usr/lib/bootstrap-studio
  launcher=/usr/share/applications/bootstrapstudio.desktop
  association=/usr/share/mime/packages/bootstrapstudio.xml
  icon_dir=/usr/share/icons/hicolor
  if [ "$1" == "--install" ]; then
    if [ "$2" != "-y" ]; then
      echo "Installing Bootstrap Studio, you're agree with the terms and conditions of the software."
      echo "Are you sure to instann Bootstrap Studio? [y/N]"
      read -r answer
      if [ "$answer" != "y" ]; then
        exit 0
      fi
    fi

    mkdir BS_DIR
    wget https://bootstrapstudio.io/releases/desktop/6.1.3/Bootstrap%20Studio.AppImage -O $BS_DIR/BootstrapStudio.AppImage
    chmod -v u+x $BS_DIR/BootstrapStudio.AppImage
    echo "" | base64 -d > $BS_DIR/16x16.png
    echo "" | base64 -d > $BS_DIR/32x32.png
    echo "" | base64 -d > $BS_DIR/64x64.png
    echo "" | base64 -d > $BS_DIR/128x128.png
    echo "" | base64 -d > $BS_DIR/256x256.png
    echo "" | base64 -d > $BS_DIR/512x512.png

    cat > $launcher <<- EOM
[Desktop Entry]
Encoding=UTF-8
Version=1.0
Type=Application
Name=Bootstrap Studio
Path=$(pwd)
Icon=bootstrapstudio
Exec=$(BS_DIR)/"BootstrapStudio.AppImage" --no-sandbox %F
Comment=A Powerful Web Design & Development Tool
Categories=Development
StartupNotify=true
StartupWMClass=Bootstrap Studio
Terminal=false
MimeType=application/x-bstudio-design;application/x-bstudio-component
EOM
    chmod -v u+x $launcher

    if command -v gio &> /dev/null; then
      gio set $launcher "metadata::trusted" true 2> /dev/null || true
    fi

    mkdir -p -v $icon_dir/16x16/{apps,mimetypes}
    mkdir -p -v $icon_dir/32x32/{apps,mimetypes}
    mkdir -p -v $icon_dir/64x64/{apps,mimetypes}
    mkdir -p -v $icon_dir/128x128/{apps,mimetypes}
    mkdir -p -v $icon_dir/256x256/{apps,mimetypes}
    mkdir -p -v $icon_dir/512x512/{apps,mimetypes}
    ln -s -v $icon_dir/16x16/apps/bootstrapstudio.png $BS_DIR/16x16.png
    ln -s -v $icon_dir/32x32/apps/bootstrapstudio.png $BS_DIR/32x32.png
    ln -s -v $icon_dir/64x64/apps/bootstrapstudio.png $BS_DIR/64x64.png
    ln -s -v $icon_dir/128x128/apps/bootstrapstudio.png $BS_DIR/128x128.png
    ln -s -v $icon_dir/256x256/apps/bootstrapstudio.png $BS_DIR/256x256.png
    ln -s -v $icon_dir/512x512/apps/bootstrapstudio.png $BS_DIR/512x512.png
    ln -s -v $icon_dir/16x16/mimetypes/bootstrapstudio-component.png $BS_DIR/16x16.png
    ln -s -v $icon_dir/32x32/mimetypes/bootstrapstudio-component.png $BS_DIR/32x32.png
    ln -s -v $icon_dir/64x64/mimetypes/bootstrapstudio-component.png $BS_DIR/64x64.png
    ln -s -v $icon_dir/128x128/mimetypes/bootstrapstudio-component.png $BS_DIR/128x128.png
    ln -s -v $icon_dir/256x256/mimetypes/bootstrapstudio-component.png $BS_DIR/256x256.png
    ln -s -v $icon_dir/512x512/mimetypes/bootstrapstudio-component.png $BS_DIR/512x512.png

    if command -v update-icon-caches &> /dev/null; then
      update-icon-caches $icon_dir
    fi

    cat > $association <<- EOM
<?xml version="1.0" encoding="UTF-8"?>
<mime-info xmlns="http://www.freedesktop.org/standards/shared-mime-info">
   <mime-type type="application/x-bstudio-design">
     <comment>Bootstrap Studio Design</comment>
     <glob pattern="*.bsdesign"/>
     <icon name="bootstrapstudio-design"/>
   </mime-type>
   <mime-type type="application/x-bstudio-component">
     <comment>Bootstrap Studio Component</comment>
     <glob pattern="*.bscomp"/>
     <icon name="bootstrapstudio-component"/>
   </mime-type>
</mime-info>
EOM

    if command -v update-mime-database &> /dev/null; then
      update-mime-database /usr/share/mime
    fi
  fi

  # uninstall
  if [ "$1" == "--uninstall" ]; then
    if [ "$2" != "-y" ]; then
      echo "Are you sure you want to uninstall Bootstrap Studio? [y/N]"
      read -r answer
      if [ "$answer" != "y" ]; then
        exit 0
      fi
    fi
    rm -v $launcher
    rm -v $association
    unlink -v $icon_dir/16x16/apps/bootstrapstudio.png
    unlink -v $icon_dir/32x32/apps/bootstrapstudio.png
    unlink -v $icon_dir/64x64/apps/bootstrapstudio.png
    unlink -v $icon_dir/128x128/apps/bootstrapstudio.png
    unlink -v $icon_dir/256x256/apps/bootstrapstudio.png
    unlink -v $icon_dir/512x512/apps/bootstrapstudio.png
    unlink -v $icon_dir/16x16/mimetypes/bootstrapstudio-component.png
    unlink -v $icon_dir/32x32/mimetypes/bootstrapstudio-component.png
    unlink -v $icon_dir/64x64/mimetypes/bootstrapstudio-component.png
    unlink -v $icon_dir/128x128/mimetypes/bootstrapstudio-component.png
    unlink -v $icon_dir/256x256/mimetypes/bootstrapstudio-component.png
    unlink -v $icon_dir/512x512/mimetypes/bootstrapstudio-component.png
    rm -rv $BS_DIR/"BootstrapStudio.AppImage"
    rm -rv $BS_DIR/*.png
    rmdir -v $BS_DIR
  fi
  exit 0
else
  echo "Please run as root"
  exit 1
fi
